import { ethers, providers } from "ethers";
import {
    Box,
    Flex,
    Avatar,
    HStack,
    Button,
} from '@chakra-ui/react';

import { Image } from '@chakra-ui/image';
import { useState } from "react";

export default function Navbar() {

    const [wallet, setWallet] = useState("")
    const [connBtnText, setConnBtnText] = useState("Connect Wallet")
    const [account, setAccount] = useState("")
    const [provider, setProvider] = useState(null);

    function connectWallet() {
        if (!window.ethereum) {
            alert("Metamask is not installed.")
            return
        }
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        setProvider

        provider.send("eth_requestAccounts", []).then(accounts => {
            setAccount(account[0])
            setConnBtnText(accounts)
        })
    }
    return (
        <Box bg={'green.200'} px={4} className='navbar'>
            <Flex h={16} alignItems={'center'} justifyContent={'space-between'}>

                <HStack spacing={8} alignItems={'center'}>
                    <Image src={'../public/logo.png'}></Image>
                    <Box pl={8} textColor={'blackAlpha.900'} >Supply Chain Control Center</Box>
                    <HStack
                        as={'nav'}
                        spacing={4}
                        display={{ base: 'none', md: 'flex' }}>
                        <Button variant={'ghost'} colorScheme={'green'} textColor={'blackAlpha.900'} >siparisler</Button>
                        <Button variant={'ghost'} colorScheme={'green'} textColor={'blackAlpha.900'} >musteriler</Button>
                    </HStack>
                </HStack>
                <HStack spacing={8} px={4}>
                    <Button onClick={() => {
                        if (account) return;
                        connectWallet();
                    }} colorScheme={'red'} variant={'solid'}>{connBtnText}</Button>
                    <Avatar
                        size={'md'}
                        src={
                            'https://miro.medium.com/max/600/1*_bLztZTVKgTeRUvkKmrGnQ.png'
                        }
                    />
                </HStack>
            </Flex>
        </Box>
    );
}
